import { Component } from '@angular/core';
import { Bug } from './bug.model';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'bug-tracker';

  selectedBug: Bug | null = null;


  onBugSelected(bug: Bug) {
    this.selectedBug = bug;
  }
}
<div class="container">
  <div class="row">
    <div class="col-md-4">
      <app-bug-detail [bug]="selectedBug"></app-bug-detail>
    </div>
    <div class="col-md-8">
      <app-bug-grid (bugSelected)="selectedBug = $event"></app-bug-grid>
    </div>
  </div>
</div>
