import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { Bug } from './bug.model';
import { model, connect, Schema } from 'mongoose';

@Injectable({
  providedIn: 'root'
})
export class BugService {
  private apiUrl = 'http://localhost:3000/api/bugs';

  constructor(private http: HttpClient) { 
    connect('mongodb+srv://willmarda:Icu4MongoDBAtlas@maincluster.twrdd3f.mongodb.net/test').then(() => {
      console.log('Connected to MongoDB Atlas');
    }).catch((err) => {
      console.error('Error connecting to MongoDB Atlas:', err);
    });

    model('Bug', new Schema<Bug>({
      title: String,
      priority: String,
      description: String,
      dateCreated: Date
    }));
  }

  getBugs(): Observable<Bug[]> {
    return this.http.get<Bug[]>(this.apiUrl);
  }

  getBug(id: string): Observable<Bug> {
    const url = `${this.apiUrl}/${id}`;
    return this.http.get<Bug>(url);
  }

  addBug(bug: Bug): Observable<Bug> {
    return this.http.post<Bug>(this.apiUrl, bug);
  }

  updateBug(id: string, bug: Bug): Observable<Bug> {
    const url = `${this.apiUrl}/${id}`;
    return this.http.put<Bug>(url, bug);
  }

  deleteBug(id: string): Observable<Bug> {
    const url = `${this.apiUrl}/${id}`;
    return this.http.delete<Bug>(url);
  }
}
