import { Component, EventEmitter, Output } from '@angular/core';
import { Bug } from '../bug.model';

@Component({
  selector: 'app-bug-grid',
  templateUrl: './bug-grid.component.html',
  styleUrls: ['./bug-grid.component.css']
})
export class BugGridComponent {

  @Output() bugSelected = new EventEmitter<Bug>();

   // Define the onBugCardClick method to set the selected bug and show the detail view
   onBugCardClick(bug: Bug) {
    this.bugSelected.emit(bug); // Emit an event with the selected bug
  }

  bugs: Bug[] = [
    {
      title: "Login page not working",
      description: "When I try to log in, the page just refreshes and nothing happens.",
      priority: "High",
      dateCreated: new Date("2022-12-15T08:30:00.000Z")
    },
    {
      title: "Unable to add items to cart",
      description: "I keep getting an error message when I try to add items to my cart.",
      priority: "Medium",
      dateCreated: new Date("2022-12-16T11:45:00.000Z")
    },
    {
      title: "Payment page not loading",
      description: "When I click on 'Pay Now', the page just keeps loading and never finishes.",
      priority: "High",
      dateCreated: new Date("2022-12-17T15:20:00.000Z")
    },
    {
      title: "Images not displaying",
      description: "The images on the product pages are not displaying properly.",
      priority: "Low",
      dateCreated: new Date("2022-12-18T10:10:00.000Z")
    },
    {
      title: "Broken link on homepage",
      description: "The link to the 'About Us' page on the homepage is broken.",
      priority: "Medium",
      dateCreated: new Date("2022-12-19T14:00:00.000Z")
    }
  ];
}
