import { Component } from '@angular/core';

interface Bug {
  title: string;
  date: Date;
  priority: string;
}

@Component({
  selector: 'app-bug-grid',
  templateUrl: './bug-grid.component.html',
  styleUrls: ['./bug-grid.component.css']
})
export class BugGridComponent {
  bugs: Bug[] = [
    {
      title: 'Bug 1',
      date: new Date(),
      priority: 'High'
    },
    {
      title: 'Bug 2',
      date: new Date(),
      priority: 'Medium'
    },
    {
      title: 'Bug 3',
      date: new Date(),
      priority: 'Low'
    }
  ];
}
